@media (max-width: 1199px) {
  .minicart--fixed {
    width: 100%;
    height: 100%;
    overflow: hidden;
  }
  .minicart {
    position: fixed;
    z-index: 200;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0,0,0,0.5);
    visibility: hidden;
    opacity: 0;
    transition: all 0.2s ease-in-out;
  }
  .minicart.minicart--actived {
    visibility: visible;
    opacity: 1;
  }
  .minicart__container {
    position: fixed;
    top: 0;
    right: 0;
    width: 300px;
    height: 100%;
    background: #fff;
    transform: translateX(100%);
    transition: all 0.2s ease-in-out;
  }
  .minicart--actived .minicart__container {
    transform: translateX(0);
  }
  .minicart__header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin: 0 10px;
    border-bottom: 1px solid #f3f3f3;
  }
  .minicart__title {
    color: #b53b96;
    flex: 1 1 auto;
    font-size: 14px;
    font-weight: 700;
    margin: 0;
    text-transform: uppercase;
  }
  .minicart__close {
    width: 45px;
    height: 45px;
    display: flex;
    justify-content: center;
    align-items: center;
    color: #b53b96;
    font-size: 20px;
  }
  .minicart__empty {
    font-weight: 500;
    text-align: center;
    padding: 20px;
  }
  .minicart__list {
    margin: 0;
    padding: 0;
  }
  .minicart__item {
    position: relative;
    max-height: 130px;
    visibility: visible;
    opacity: 1;
    transition: all 0.4s ease-in-out;
  }
  .minicart__item + .minicart__item {
    border-top: solid 1px #eee;
  }
  .minicart__link {
    display: flex;
    align-items: center;
    list-style: none;
    padding: 10px;
    color: #666;
  }
  .minicart__link:hover {
    text-decoration: none;
  }
  .minicart__info {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    align-items: center;
  }
  .minicart__name {
    font-size: 14px;
    flex: 0 0 100%;
    font-family: Montserrat;
    font-size: 12px;
    color: #211915;
    letter-spacing: 0;
  }
  .minicart__image {
    flex: 0 0 50px;
    margin-right: 20px;
  }
  .minicart__qty {
    font-size: 10px;
  }
  .minicart__price {
    font-size: 14px;
    color: #f00;
  }
  .minicart__total {
    display: flex;
    height: 45px;
    align-items: center;
    justify-content: space-between;
    font-size: 14px;
    font-weight: 700;
    padding: 0 20px;
  }
  .minicart__main {
    height: calc(100% - 140px);
    overflow-y: auto;
  }
  .minicart__finish {
    background: #3aad27;
    color: #fff;
    font-size: 16px;
    font-weight: 700;
    text-transform: uppercase;
    height: 50px;
    display: flex;
    justify-content: center;
    align-items: center;
  }
  .minicart__finish:hover {
    background: #339021;
  }
  .minicart__qty:before {
    content: ' / ';
  }
}
@media (min-width: 1200px) {
  #cart .dropdown:hover .cart__dropdown {
    opacity: 1;
    visibility: visible;
  }
  .minicart__list {
    margin-bottom: 10px;
    max-height: 250px;
    overflow-y: auto;
    max-width: 100%;
  }
  .minicart__footer {
    border-top: solid 1px #949393;
    padding: 19px;
    display: flex;
    flex-direction: column;
    align-items: center;
/* Font */
    font-family: Montserrat;
    font-size: 18px;
    font-weight: normal;
    font-stretch: normal;
    font-style: normal;
    line-height: 1.2;
    letter-spacing: normal;
    text-align: left;
    color: #000;
  }
  .minicart__total {
    display: flex;
    flex-direction: column;
    align-items: center;
/* Font */
    font-family: Montserrat;
    font-size: 18px;
    font-weight: normal;
    font-stretch: normal;
    font-style: normal;
    line-height: 1.2;
    letter-spacing: normal;
    text-align: center;
    color: #000;
  }
  .minicart__total-price {
    color: #ff6b6b;
    margin-top: 6px;
    margin-bottom: 20px;
  }
  .minicart__finish {
    height: 57px;
    background-color: #6bcf60;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 0 19px;
    border-radius: 10px;
/* Font */
    font-family: Montserrat;
    font-size: 18px;
    font-weight: normal;
    font-stretch: normal;
    font-style: normal;
    line-height: 1.2;
    letter-spacing: normal;
    text-align: center;
    color: #fff;
  }
  .minicart__main {
    padding: 19px;
  }
  .minicart__info {
    position: relative;
  }
  .minicart__delete {
    position: absolute;
    right: -14px;
    top: 0;
    width: 14px;
    height: 14px;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 0;
/* Font */
    font-family: Montserrat;
    font-size: 12px;
    font-weight: normal;
    font-stretch: normal;
    font-style: normal;
    line-height: 1;
    letter-spacing: normal;
    text-align: left;
    color: #f00;
    background-color: transparent;
  }
  .minicart__delete:hover {
    color: #000;
  }
  .minicart__name {
    font-family: Montserrat;
    font-size: 12px;
    font-weight: normal;
    font-stretch: normal;
    font-style: normal;
    line-height: 1.2;
    letter-spacing: normal;
    text-align: left;
    color: #211915;
  }
  .minicart__details {
    display: flex;
    flex-direction: column;
  }
  .minicart__price {
    font-family: Montserrat;
    font-size: 14px;
    font-weight: normal;
    font-stretch: normal;
    font-style: normal;
    line-height: 1.2;
    letter-spacing: normal;
    text-align: left;
    color: #ff6b6b;
    margin: 11px 0 13px 0;
  }
  .minicart__qty {
    font-family: Montserrat;
    font-size: 11px;
    font-weight: normal;
    font-stretch: normal;
    font-style: normal;
    line-height: 1.2;
    letter-spacing: normal;
    text-align: left;
    color: #bcbbba;
  }
}
.minicart__item {
  padding: 11px 0;
  overflow: hidden;
}
.minicart__item:last-child {
  border-bottom: 0 none;
}
.minicart__link {
  display: flex;
}
.minicart__image {
  display: flex;
  flex: 0 0 auto;
  justify-content: center;
  margin-right: 15px;
  position: relative;
  width: 80px;
}
.minicart__image img {
  max-height: 80px;
  max-width: 80px;
}
.minicart__info {
  flex: 1 1 auto;
}
.minicart__details {
  align-items: baseline;
  display: flex;
  justify-content: space-between;
  margin-bottom: 10px;
}
.minicart__price .minicart__qty {
  color: #e2b22b;
  font-size: 13px;
  font-weight: 600;
  line-height: 13px;
}
.minicart__price .minicart__total-label {
  color: #403232;
  display: block;
  font-size: 13px;
  font-weight: 600;
  line-height: 13px;
  margin-bottom: 2px;
  text-align: center;
  text-transform: uppercase;
}
.minicart__price .minicart__total-price {
  color: #e2b22b;
  display: block;
  font-size: 16px;
  font-weight: 600;
  line-height: 16px;
  text-align: center;
}
.minicart__price .minicart__finish {
  background: #75da5b;
  border: 1px solid #75da5b;
  border-radius: 3px;
  box-shadow: 2px 2px 10px 0 rgba(92,19,30,0.15);
  color: #fff;
  display: block;
  font-size: 16px;
  font-weight: 600;
  line-height: 16px;
  margin-top: 25px;
  padding: 16px 20px;
  text-align: center;
  text-transform: uppercase;
  width: auto;
}
.minicart__price .minicart__finish:hover {
  background: #fff;
  color: #75da5b;
}
.minicart__header {
  display: none;
}
.cart__dropdown {
  position: absolute;
  right: 0;
  background: #fff;
  z-index: 99999999;
  width: 350px;
  padding: 0;
  border-radius: 10px;
  border: solid 1px #bcbbba;
  background-color: #fff;
  top: 100%;
  display: flex;
  opacity: 0;
  visibility: hidden;
  transition: 0.25s ease-in-out;
  transition-property: opacity, visibility, transform;
}
.cart {
  position: relative;
}
.cart__icon--catalog path,
.cart__icon--catalog rect {
  fill: #ff8484;
}
.cart:hover .cart__dropdown {
  display: block;
}
